import { Cinema } from './Cinema';
import { Cliente } from './Cliente';
var readline = require("readline-sync");
export class Controlador {
    comandos(cmd) {
        switch (cmd) {
            case "iniciar": {
                let quantidade = readline.question("Informe a quantidade de assentos da sala: ");
                this.cinema = new Cinema(quantidade);
                break;
            }
            case "show": {
                this.cinema.show();
                break;
            }
            case "reservar": {
                let nome = readline.question("Informe o nome do cliente: ");
                let fone = readline.question("Informe o fone do cliente: ");
                let assento = readline.question("Informe o assento: ");
                let cliente = new Cliente(nome, fone);
                this.cinema.reservar(cliente, assento);
                break;
            }
            case "cancelar": {
                let nome = readline.question("Informe o nome do cliente: ");
                this.cinema.cancelar(nome);
                break;
            }
            default: {
                console.log("failure: comando invalido");
                break;
            }
        }
    }
    executar() {
        while (true) {
            this.opcoes();
            let cmd = readline.question("Informe o comando: ");
            if (cmd != "end") {
                this.comandos(cmd);
            }
            else {
                return;
            }
        }
    }
    opcoes() {
        console.log("\n");
        console.log("iniciar - inicializa uma nova sala");
        console.log("show - mostrar sala");
        console.log("reservar - faz reserva para cliente");
        console.log("cancelar - cancela reserva do cliente");
        console.log("end - sair do programa");
        console.log("\n");
    }
}
