export class Cinema {
    constructor(qtd) {
        this.clientes = new Array();
        for (let i = 0; i < qtd; i++) {
            this.clientes.push(null);
        }
    }
    reservar(cliente, assento) {
        for (let i = 0; i < this.clientes.length; i++) {
            if (this.clientes[i] != null) {
                if (this.clientes[i].getId() == cliente.getId()) {
                    console.log("Falha: Já existe um cliente com esse nome");
                    return;
                }
            }
        }
        if (this.clientes[assento - 1] == null) {
            this.clientes[assento - 1] = cliente;
        }
        else {
            console.log("Falha: A cadeira já está ocupada");
        }
    }
    cancelar(cliente) {
        for (let i = 0; i < this.clientes.length; i++) {
            if (this.clientes[i] != null) {
                if (this.clientes[i].getId() == cliente) {
                    this.clientes[i] = null;
                    return;
                }
            }
        }
        console.log("Falha: O cliente não está no cinema");
    }
    show() {
        let res = "[ ";
        for (let i = 0; i < this.clientes.length; i++) {
            if (this.clientes[i] == null) {
                res += "- ";
            }
            else {
                res += this.clientes[i].toString() + " ";
            }
        }
        res += "]";
        console.log(res);
    }
}
